import React, {useState, useEffect} from 'react';
import {Link, useHistory} from 'react-router-dom';
import {postDog, getTemperaments} from '../actions/index.js';
import { useDispatch, useSelector} from 'react-redux';



const validate = (input) =>  {
    let errors = {};
    if (!input.name) {
        errors.name = "Name requiered";
    } else if (!input.height || input.height < 0) {
        errors.height = "Must be higher than 0";
    } else if (!input.weight || input.weight < 0) {
        errors.weight = "Must be higher than 0";
    } else if (!input.life_span) {
        errors.life_span = "Life Span requiered";
    }

    return errors;
}

export default function DogCreated () {
    const dispatch = useDispatch();
    const history = useHistory();
    const temperament = useSelector((state) => state.temperaments);
    const [errors, setErrors] = useState({});

    const [input, setInput] = useState({
        name: "",
        weight: "",
        height: "",
        life_span: "",
        temperament: []
    })

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    const handleChange = (e) => {
        setInput({
            ...input,
            [e.target.name] : e.target.value,
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    const handleCheckBox = (e) => {
        if (e.target.checked) {
            setInput({
                ...input,
                temperament: input.temperament.concat(e.target.value)
            })
        }
    }


    const handleSubmit = (e) => {
        e.preventDefault()
        let errors = Object.keys(validate(input))
        if (errors.length !== 0) {
            alert('Rellenar los campos')
        } else {
            dispatch(postDog(input))
            alert("Dog Created!")
            setInput({
                name: "",
                weight: "",
                height: "",
                life_span: "",
                temperament: []
            })
            history.push('/dogs')
        }
    }

    return (
        <div>
            <div>
                <h1>Create your own dog!</h1>
            </div>
            <form onSubmit={(e) => handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                </div>
                    <input type="text" value = {input.name} name="name" onChange={handleChange}/>
                    {errors.name && (
                        <div>
                            <p className="error">{errors.name}</p>
                        </div>
                    )}
                <div>
                    <div>
                        <label>Weight:</label>
                    </div>
                    <input type="float" value = {input.weight} name="weight" onChange={handleChange}/>
                    {errors.weight && (
                        <div>
                            <p className="error">{errors.weight}</p>
                        </div>
                    )}
                </div>
                <div>
                    <div>
                        <label>Height:</label>
                    </div>
                    <input type="float" value = {input.height} name="height" onChange={handleChange}/>
                    {errors.height && (
                        <div>
                            <p className="error">{errors.height}</p>
                        </div>
                    )}
                </div>
                <div>
                    <div>
                        <label>Life Span:</label>
                    </div>
                    <input type="float" value = {input.life_span} name="life_span" onChange={handleChange}/>
                    {errors.life_span && (
                        <div>
                            <p className="error">{errors.life_span}</p>
                        </div>
                    )}
                </div>

                <div>
                    <label>
                        {temperament.map((e, index) => { 
                            return (
                            <div key = {e.name}>
                                <span>{e.name}</span>
                                <input type="checkbox" name = {e.name} value = {index + 1} onChange = {handleCheckBox}/>
                            </div>
                        )})}
                    </label>
                </div> 

                <button type="submit">Create dog</button>
            </form>
            <div >
                <Link to="/dogs"><button>Back to HomePage</button></Link>
            </div>
        </div>
    )

}
