import React, {useState, useEffect} from 'react';
import {Link, useHistory} from 'react-router-dom';
import {postDog, getTemperaments} from '../actions/index.js';
import { useDispatch, useSelector} from 'react-redux';
import Style from "./DogCreated.module.css";



const validate = (input) =>  {
    let errors = {};
    if (!input.name) {
        errors.name = "Name requiered";
    } else if (!input.weight) {
        errors.weight = "Weight required";
    } else if (!input.height) {
        errors.height = "Height required";
    } else if (!input.life_span) {
        errors.life_span = "Life Span requiered";
    }

    return errors;
}

export default function DogCreated () {
    const dispatch = useDispatch();
    const history = useHistory();
    const temperament = useSelector((state) => state.temperaments);
    const [errors, setErrors] = useState({});

    const [input, setInput] = useState({
        name: "",
        weight: "",
        height: "",
        life_span: "",
        temperaments: [],
        createdInDb: true,
    })

    useEffect(() => {
        dispatch(getTemperaments());
    }, []);

    const handleChange = (e) => {
        setInput({
            ...input,
            [e.target.name] : e.target.value,
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    const handleCheckBox = (e) => {
        if (e.target.checked) {
            setInput({
                ...input,
                temperaments: input.temperaments.concat(e.target.value)
            })
        }
    }

    const handleSelect = (e) => {
        setInput({
            ...input,
            temperaments: input.temperaments.concat(e.target.value)
        })
    }


    const handleSubmit = (e) => {
        e.preventDefault()
        let errors = Object.keys(validate(input))
        if (errors.length !== 0) {
            alert('Rellenar los campos')
        } else {
            dispatch(postDog(input))
            alert("Dog Created!")
            setInput({
                name: "",
                weight: "",
                height: "",
                life_span: "",
                temperaments: [],
                createdInDb: true
            })
            history.push('/dogs')
        }
    }

    return (
        <div>
            <div>
                <h1 className={Style.titleCreateDog}>Create your own dog!</h1>
            </div>
            <form onSubmit={(e) => handleSubmit(e)} className={Style.box}>
                <div>
                    <label>Name:</label>
                </div>
                    <input type="text" value = {input.name} name="name" onChange={(e) => handleChange(e)}/>
                    {errors.name && (
                        <div>
                            <p className="error">{errors.name}</p>
                        </div>
                    )}
                <div>
                    <div>
                        <label>Weight:</label>
                    </div>
                    <input type="text" value = {input.weight} name="weight" onChange={(e) => handleChange(e)}/>
                    {errors.weight && (
                        <div>
                            <p className="error">{errors.weight}</p>
                        </div>
                    )}
                </div>
                <div>
                    <div>
                        <label>Height:</label>
                    </div>
                    <input type="text" value = {input.height} name="height" onChange={(e) => handleChange(e)}/>
                    {errors.height && (
                        <div>
                            <p className="error">{errors.height}</p>
                        </div>
                    )}
                </div>
                <div>
                    <div>
                        <label>Life Span:</label>
                    </div>
                    <input type="text" value = {input.life_span} name="life_span" onChange={(e) => handleChange(e)}/>
                    {errors.life_span && (
                        <div>
                            <p className="error">{errors.life_span}</p>
                        </div>
                    )}
                </div>

                <div>
                    {/* <label>
                        {temperament.map((e) => { 
                            return (
                            <div key = {e.name}>
                                <span>{e.name}</span>
                                <input type="checkbox" name = {e.name} value = {e.name} onChange = {(e) => handleCheckBox(e)}/>
                            </div>
                        )})}
                    </label> */}
                    <select onChange={(e) => handleSelect(e)} className = {Style.temps}>
                        {temperament.map((e) => { 
                            return (
                                <option value={e.name}>{e.name}</option>
                            )
                        })}
                    </select>
                    <div style={{listStyle: 'none'}}>
                        <ul><li>{input.temperaments.map((e) => e + " ,")}</li></ul>
                    </div>
                </div> 
                <button  className = {Style.buttonCreateDog} type="submit">Create dog</button>
            </form>
            <div >
                <Link to="/dogs"><button className={Style.buttonToHome}>Back to HomePage</button></Link>
            </div>
        </div>
    )

}
