import React from "react";
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {getDogs, getDogById, getDogsByName} from "../actions/index.js";
import {Link} from "react-router-dom";
// import RecipeCard from "./Card.jsx"
import Paged from "./Paging.jsx";
// import SearchBar from "./SearchBar";

export default function Home () {
    const dispatch = useDispatch();
    const dogs = useSelector((state) => state.dogs)
    const [currentPage, setCurrentPage] = useState(1);
    const [dogsPerPage, setDogsPerPage] = useState(8);

    const paging = (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    useEffect (() => {
        dispatch(getDogs())
    }, [])

    const indexOfLastDog = currentPage * dogsPerPage;
    const indexOfFirstDog = indexOfLastDog - dogsPerPage;
    const currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog);

    return (
        <div>
            <div>
                <Link to="/dog"><button>Create your own dog!</button></Link>
            </div>
            <div>
                <h1>Doggy's main page!</h1>
            </div>
            <div>
                <select>
                    <option value="None">Sort by letter</option>
                    <option value="Asc">Ascendant</option>
                    <option value="Desc">Descendant</option>
                </select>
                <select>
                    <option value="None">Filter by weight</option>
                    <option value="Asc">Ascendant</option>
                    <option value="Desc">Descendant</option>
                </select>
            </div>
            <div>
                <div>
                    <Paged
                    dogsPerPage={dogsPerPage}
                    dogs = {dogs.length}
                    paged = {paging}
                    />
                </div>
            </div>
        </div>
    )






}
