const initialState = {
    dogs: [],
    allDogs: [],
    dogsById: {},
    temperaments: [],
}

function rootReducer (state = initialState, action) {
    switch(action.type) {
        case 'GET_DOGS':
            state = initialState
            return {
                ...state,
                dogs: state.dogs.concat(action.payload),
                allDogs: state.allDogs.concat(action.payload)
            }

        case 'GET_DOGS_BY_NAME':
            return {
                ...state,
                dogs: action.payload
            }

        case 'GET_DOG_BY_ID':
            return {
                ...state,
                dogsById: action.payload
            }

        case 'GET_TEMPERAMENTS':
            return {
                ...state,
                templates: action.payload
            }

        case 'SORT_BY_WEIGHT':

            const sortWeight = action.payload === 'None' ? state.dogs : action.payload === 'Asc' ? state.dogs.sort(function (a,b) {
                if (a.weight > b.weight) {
                    return 1;
                }
                if (a.weight < b.weight) {
                    return -1;
                }
                return 0;
            })
            :
            state.dogs.sort(function (a,b) {
                if (a.weight > b.weight) {
                    return -1;
                }
                if (a.weight < b.weight) {
                    return 1;
                }
                return 0;
            });
            state.dogs = [];
            return {
                ...state,
                dogs: state.dogs.concat(sortWeight)
            }

        case 'SORT_BY_LETTER': 
            const sortLetter = action.payload === 'None' ? state.dogs : action.payload ===  'Asc' ? state.dogs.sort(function(a, b) {
                if (a.name > b.name) {
                    return 1;
                }
                if (a.name < b.name) {
                    return -1;
                }
                return 0;
            })
            :
            state.dogs.sort(function (a, b) {
                if (a.name > b.name) {
                    return -1;
                }
                if (a.name < b.name) {
                    return 1;
                }
                return 0;
            });
            state.dogs = [];
            return {
                ...state,
                dogs: state.dogs.concat(sortLetter)
            }


        default:
            return state;
    }
}

export default rootReducer;
